import { useState } from "react";
import { UploadFormProps } from "../../../interfaces";

export const CategoryForm = (props: UploadFormProps) => {
    const { onSubmit, onCancel } = props;
    const [ctg_desc, setctg_desc] = useState("");


    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        const now = new Date();
        const ctg_date = now.toLocaleDateString();
        e.preventDefault();
        onSubmit({  ctg_desc, created_at: ctg_date, updated_at: ctg_date });
    };

    return (
        <form onSubmit={handleSubmit}>

        <div className="form-group">
            <label htmlFor="ctg_desc">ctg_desc</label>
            <input
            type="text"
            className="form-control"
            id="ctg_desc"
            value={ctg_desc}
            onChange={(e) => setctg_desc(e.target.value)}
            />
        </div>
        <button type="submit" className="btn btn-primary">
            Submit
        </button>
        <button type="button" className="btn btn-secondary" onClick={onCancel}>
            Cancel
        </button>
        </form>
    );
    };
