import { MapForm } from "../components/Forms/CRUD/MapForm";
import api from "../services/api";
import { onSubmitProps, User } from "../interfaces";
import { Auth } from "../hooks/useAuth";

async function onSubmit(props: onSubmitProps) {

    const { user, err, check } = Auth<User | null>('api/Auth/perfil');

    if (!props.file || !props.map) {
        return;
    }

    try{
        const formData = new FormData();

        formData.append('file', props.file);
        formData.append('request', JSON.stringify(props.map));


        await api.post('/api/Data/mapas', {
            data: formData,
            headers: {
                'Content-Type': 'multipart/form-data',
                'Authorization': 'Bearer ' + user?.token,
            },
        });
        window.location.href='/'+props.map?.map_id;
    } catch (err) {
        console.log(err);
    }
}

function onCancel() {
    console.log("Cancel");
}

export const Upload: React.FC = () => {


    return (
        <MapForm {...{onSubmit, onCancel}}/>
    );
}
