import { useFetch } from "../../hooks/useFetching";
import api from "../../services/api";
import { Publication } from "../../types";
import { NotFound } from "../NotFound/NotFound";


function onDownloadClick(id: number){
    const url = 'api/Data/publications/download/' + id;

    return api.get(url, {
        responseType: "blob",
    });
}

export const Publications = () => {

    const url = 'api/Data/publications';
    const { data: publications, err } = useFetch<Publication[]>(url);

    if(!err){
        return(
            <div>
                <h2>Publicações</h2>
                <br/>
                <ul>
                    {publications?.map((publication) => {
                        return(
                            <li key={publication.pub_id}>
                                <h4 className="subtitle">{publication.pub_title}</h4>
                                <p>{publication.pub_desc}</p>
                                <p>{publication.pub_authors}</p>
                                <p>{publication.pub_date}</p>
                                <p>{publication.pub_type}</p>
                                <p>{publication.pub_link}</p>
                                <button onClick={() => onDownloadClick(publication.pub_id)}>Download</button>
                            </li>
                        )
                    })}
                </ul>
            </div>
        )
    }else{
        return(
            <NotFound/>
        )
    }
}
