import { useState } from "react";
import { UploadFormProps } from "../../../interfaces";
import { Form } from "../AuthForms/style";
import { Map } from "../../../types";


export const MapForm = (props: UploadFormProps) => {

    const { onSubmit, onCancel } = props;

    const [description, setDescription] = useState("");
    const [atr, setAtr] = useState("");
    const [leg, setLeg] = useState("");
    const [ref, setRef] = useState("");
    const [choro, setChoro] = useState(0);
    const [map, setMap] = useState<Map>({
        map_atr: "",
        map_ctg: "",
        map_desc: "",
        map_id: "",
        map_legs: null,
        map_refs: null,
    })

    const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        onSubmit({map_atr: map.map_atr, map_ctg: map.map_ctg, map_desc: map.map_desc, map_id: map.map_id, map_legs: map.map_legs, map_refs: map.map_refs, choropleth: map.choropleth});
    };
    return (
        <div>

            <h2>Inserção de Dados Relacionados a um Mapa</h2>
            <Form className="box" method="post" onSubmit={handleSubmit}>
                <h3>Cadastrar Mapa</h3>
                <input type="text" value={description} placeholder="Nome do Mapa" onChange={(e) =>setDescription(e.target.value) }/>
                <input type="text" value={atr} placeholder="Atributo" onChange={(e) => setAtr(e.target.value) }/>
                <input type="checkbox" id="choro" value={choro} onChange={(e) => setChoro(Number(!choro)) }/>
                <label htmlFor="choro" style={{"float":"revert", "fontSize":"6px"}}>O mapa se baseia em uma imagem estática</label>
                <button type="button" value="Cadastrar"> Cadastrar </button>
                <button type="button" onClick={onCancel} value="Cancelar"> Cancelar </button>
            </Form>

        </div>
    );
    };
